
public class forwhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<=10; i++)
			System.out.println(i);
		System.out.println("-----------------");
		for(int j=10;j>=0;j--)
			System.out.println(j);
		
		System.out.println("-----------------");
		
		
		int c =10;
		
		while(c>0){
			System.out.println(c);
			c--;
		}
		System.out.println("-----------------");
		
		
		
		do {
			System.out.println(c);
			c--;
			
		}while(c>0);

	}

}
